<?php//В класс Car из предыдущих заданий добавить методы showBrand(), showModel() которые выводят на экран значения свойств brand, model соответственно. В класс User из предыдущих заданий добавить свойство isLogged, которое принимает true, если пользователь успешно авторизировался, и false при выходе из системы. Задать значения для этого свойства в методах login(), logout().Class Car {    public $brand;    public $model;    public $year;    public $driver;    public function showBrand(){        echo $this->brand."<br>";    }    public function showModel(){        echo $this->model."<br>";    }    }class user{    public $login;    public $logout;    public $password;    public $email;    public $rating = 0;    public function isLogged()    {        if ($this->login) {            echo "Login is ".$this->login."<br>"."Logout is ".$this->logout."<br>";return TRUE;        } elseif ($this->logout) {            echo "Login is ".$this->login."<br>"."Logout is ".$this->logout."<br>";return FALSE;        } else echo "Login and logout can no be both TRUE or FALSE at the same time";    }}$mers = new Car();$mers -> brand = "Mersedes Bens";$mers -> model = "Vito";$mers ->showBrand();$mers ->showModel();$Max = new user();//$Max -> logout = TRUE;$Max -> login = TRUE;var_dump($Max ->isLogged());