<?php//Изменить область видимости свойств класса User на protected. Унаследовать от класса User классы Manager, Admin. Создать объекты менеджера и админа, задать значения для свойств объектов.class User{    protected $login;    protected $logout;    protected $password;    protected $email;    protected $rating;}class Manager extends User{    public $position;    public function say(){        echo "Hi there, I am a manager.";    }};class Admin extends User{    public $position;    public function say(){        echo "Hi there, I am an administrator.";    }};$taras = new Manager;$dima = new Admin;$dima -> position = "teacher";$taras -> position = "student";echo "<pre>";var_dump($taras);echo "<br>";var_dump($dima);echo "</pre>";