<?php//Для класса User из предыдущего занятия создать методы login(), logout(), которые просто выводят на экран соответствующее сообщение. Создать экземпляр класса, вызвать созданные методы.class user {    public $login;    public $password;    public $email;    public $rating = 0;    public function login()    {        echo "Login is ".$this->login."<br>";    }    public function logout($something) {        echo "Something about logout - ".$something;    }}$Max = new user();$Max->login = "MMaaxx";$Max -> login();$Max -> logout("blablabla");?>