<?phpclass Pet {    public $name;    public $color;    public $voice;    public $price;    private $tail = TRUE;    public function cutTale(){        $this->tail="FALSE";    }    public function Say(){        echo $this->name." says ".$this->voice;    }}$cat = new Pet();$cat ->name = "Dana";$cat ->voice = "Meow!";$cat->Say();$cat->tail="FALSE";echo "<br>";$dog = new Pet();$dog ->name = "Mike";$dog ->voice = "WOW!";$dog->Say();?>